	-- VIN
	UPDATE fin_vendor_invoice
	INNER JOIN NRF_ ON fin_vendor_invoice.BranchCode = NRF_.BranchCode
		AND fin_vendor_invoice.Code = NRF_.DocumentNo
		AND NRF_.DocumentType = 'VIN'
